-- Table: Spells.Arcane

CREATE SCHEMA IF NOT EXISTS "Spells";

DROP TABLE IF EXISTS "Spells"."Arcane";

CREATE TABLE IF NOT EXISTS "Spells"."Arcane"
(
    "SpellArcKey" integer NOT NULL GENERATED ALWAYS AS IDENTITY ( INCREMENT 1 START 0 MINVALUE 0 MAXVALUE 2147483647 CACHE 1 ),
    "Level" integer,
    "Category" text,
    "Name" text,
    "School" text,
    "Range" text,
    "Duration" text,
    "AreaOfEffect" text,
    "Components" text,
    "CastingTime" text,
    "Save" text,
    "Desc" text,
    "Freq" text,
    "Src" text,
    "Page" integer,
    "Special" text,
    "Other" text,
    "Notes" text,
    
CONSTRAINT "SpellArcKey_pkey" PRIMARY KEY ("SpellArcKey")
)

TABLESPACE pg_default;

ALTER TABLE IF EXISTS "Spells"."Arcane"
    OWNER to steven;
    
